(308, 1, '2020-03-30 12:59:57', '2020-03-30 12:59:57', '{\"id\":308,\"field_id\":5,\"fields\":{\"0\":{\"id\":\"0\",\"type\":\"name\",\"label\":\"Name\",\"format\":\"simple\",\"description\":\"\",\"required\":\"1\",\"size\":\"large\",\"simple_placeholder\":\"Your Name\",\"simple_default\":\"\",\"first_placeholder\":\"\",\"first_default\":\"\",\"middle_placeholder\":\"\",\"middle_default\":\"\",\"last_placeholder\":\"\",\"last_default\":\"\",\"label_hide\":\"1\",\"css\":\"wpforms-one-half wpforms-first\"},\"3\":{\"id\":\"3\",\"type\":\"email\",\"label\":\"Email\",\"description\":\"\",\"required\":\"1\",\"size\":\"large\",\"placeholder\":\"Your Email\",\"confirmation_placeholder\":\"\",\"label_hide\":\"1\",\"default_value\":\"\",\"css\":\"wpforms-one-half\"},\"4\":{\"id\":\"4\",\"type\":\"text\",\"label\":\"Subject\",\"description\":\"\",\"size\":\"large\",\"placeholder\":\"Subject\",\"label_hide\":\"1\",\"default_value\":\"\",\"css\":\"\",\"input_mask\":\"\"},\"2\":{\"id\":\"2\",\"type\":\"textarea\",\"label\":\"Message\",\"description\":\"\",\"required\":\"1\",\"size\":\"medium\",\"placeholder\":\"Your Message\",\"label_hide\":\"1\",\"css\":\"\"}},\"settings\":{\"form_title\":\"Hestia\",\"form_desc\":\"\",\"form_class\":\"\",\"submit_text\":\"Send message\",\"submit_text_processing\":\"Sending...\",\"submit_class\":\"\",\"notification_enable\":\"1\",\"notifications\":{\"1\":{\"email\":\"{admin_email}\",\"subject\":\"New Entry: Zerif\",\"sender_name\":\"{field_id=\\\"0\\\"}\",\"sender_address\":\"{admin_email}\",\"replyto\":\"{field_id=\\\"1\\\"}\",\"message\":\"{all_fields}\"}},\"confirmation_type\":\"message\",\"confirmation_message\":\"Thanks for contacting us! We will be in touch with you shortly.\",\"confirmation_message_scroll\":\"1\",\"confirmation_page\":\"9\",\"confirmation_redirect\":\"\"},\"meta\":{\"template\":\"contact\"}}', 'Hestia', '', 'publish', 'closed', 'closed', '', 'hestia', '', '', '2020-03-30 12:59:58', '2020-03-30 12:59:58', '', 0, 'http://loca